    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String inputstr = jTextAreaInput.getText();
        List<String> choicelist = getList(inputstr);
        int choicenum = choicelist.size();
        logln("可选项数目：" + choicenum);
        int nums = Integer.parseInt(jFormattedTextFieldNums.getText());
        if (nums < 1) {
            nums = 1;
        }
        if (nums > choicenum) {
            logln("我从哪里多摸几个选项啊？给你随机一个吧");
            nums = 1;
        }
        Set<Integer> intset = new HashSet<Integer>();
        Random rand = new Random(System.currentTimeMillis());
        while (intset.size() < nums) {
            int tempi = rand.nextInt(choicenum);
//            logln("随机数" + tempi);
//            if (intset.contains(tempi)) {
//                logln("已存在");
//            }
            intset.add(tempi);
        }
        logln("随机结果为：" );
        for (Iterator<Integer> it = intset.iterator(); it.hasNext();) {
            Integer i = it.next();
            logln(choicelist.get(i));
        }


    }//GEN-LAST:event_jButton1ActionPerformed

