    private void log(String s) {
        jTextAreaOutput.append(s);
    }

    private void logln(String s) {
        jTextAreaOutput.append(s);
        jTextAreaOutput.append("\r\n");
    }

    private List<String> getList(String inputstr) {
        String[] inputsl = inputstr.split("\r|\n");
        List<String> choicelist = new ArrayList<String>();
        for (int i = 0; i < inputsl.length; i++) {
            String string = inputsl[i];
            if (string.trim().length() != 0) {
                choicelist.add(string);
            }
        }
        return choicelist;
    }
    
